refentry.refsect1:
  &reftitle.description;

&style.oop;
  intmysqli_result->field_count

&style.procedural;

   intmysqli_num_fields
   mysqli_resultresult



   Gib die Anzahl der Spalten in der Ergebnismenge zurück.

  &reftitle.examples;

   &style.oop;


<?php

mysqli_report(MYSQLI_REPORT_ERROR | MYSQLI_REPORT_STRICT);
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

$result = $mysqli->query("SELECT Name, CountryCode, District, Population FROM City ORDER BY ID LIMIT 1");

/* Anzahl der Spalten in der Ergebnismenge ermitteln */
$field_cnt = $result->field_count;

printf("Die Ergebnismenge enthält %d Spalten.\n", $field_cnt);




   &style.procedural;


<?php

mysqli_report(MYSQLI_REPORT_ERROR | MYSQLI_REPORT_STRICT);
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

$result = mysqli_query($link, "SELECT Name, CountryCode, District, Population FROM City ORDER BY ID LIMIT 1");

/* Anzahl der Spalten in der Ergebnismenge ermitteln */
$field_cnt = mysqli_num_fields($result);

printf("Die Ergebnismenge enthält %d Spalten.\n", $field_cnt);


   &examples.outputs;


Die Ergebnismenge enthält 4 Spalten.
