refentry.refsect1:
&reftitle.returnvalues;


 Gibt einen Integer zurück, der die Anzahl der Datensätze in der
 Ergebnismenge angibt.

  &reftitle.examples;

   &style.oop;


<?php
/* Datenbankverbindung öffnen */
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Datenbankverbindung prüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20";
if ($stmt = $mysqli->prepare($query)) {

    /* Abfrage ausführen */
    $stmt->execute();

    /* Ergebnis abrufen */
    $stmt->store_result();

    printf("Die Anzahl der Datensätze: %d.\n", $stmt->num_rows);

    /* Abfrage-Objekt schließen */
    $stmt->close();
}

/* Verbindung schließen */
$mysqli->close();
?>




   &style.procedural;


<?php
/* Datenbankverbindung öffnen */
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Datenbankverbindung prüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20";
if ($stmt = mysqli_prepare($link, $query)) {

    /* Abfrage ausführen */
    mysqli_stmt_execute($stmt);

    /* Ergebnis abrufen */
    mysqli_stmt_store_result($stmt);

    printf("Die Anzahl der Datensätze: %d.\n", mysqli_stmt_num_rows($stmt));

    /* Abfrage-Objekt schließen */
    mysqli_stmt_close($stmt);
}

/* Verbindung schließen */
mysqli_close($link);
?>


   &examples.outputs;


Die Anzahl der Datensätze: 20.
