refentry.refsect1:
&reftitle.returnvalues;


 Gibt im Erfolgsfall &true; zurück, &false; wird zurückgegeben, wenn keine
 weiteren Zeilen mehr in statement vorhanden sind.

  &reftitle.examples;



    oci_fetch mit definierten Variablen


<?php

$conn = oci_connect('hr', 'welcome', 'localhost/XE');
if (!$conn) {
    $e = oci_error();
    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);
}

$sql = 'SELECT location_id, city FROM locations WHERE location_id < 1200';
$stid = oci_parse($conn, $sql);

// Die Definitionen MÜSSEN vor der Ausführung erfolgen
oci_define_by_name($stid, 'LOCATION_ID', $locid);
oci_define_by_name($stid, 'CITY', $city);

oci_execute($stid);

// Jedes Abrufen füllt die zuvor definierten Variablen mit den Daten der
// nächsten Zeile
while (oci_fetch($stid)) {
    echo "Standort-ID $locid ist $city<br>\n";
}

// Ausgabe:
//   Standort-ID 1000 ist Rom
//   Standort-ID 1100 ist Venedig

oci_free_statement($stid);
oci_close($conn);

?>







    oci_fetch mit oci_result


<?php

$conn = oci_connect('hr', 'welcome', 'localhost/XE');
if (!$conn) {
    $e = oci_error();
    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);
}

$sql = 'SELECT location_id, city FROM locations WHERE location_id < 1200';
$stid = oci_parse($conn, $sql);
oci_execute($stid);

while (oci_fetch($stid)) {
    echo oci_result($stid, 'LOCATION_ID') . " ist ";
    echo oci_result($stid, 'CITY') . "<br>\n";
}

// Ausgabe:
//   1000 ist Rom
//   1100 ist Venedig

oci_free_statement($stid);
oci_close($conn);

?>
