  &reftitle.examples;



    pg_fetch_all-Beispiel


<?php
$conn = pg_pconnect("dbname=publisher");
if (!$conn) {
    echo "Konnte keine Verbindung aufbauen.\n";
  exit;
}

$result = pg_query($conn, "SELECT * FROM authors");
if (!$result) {
    echo "Ein Fehler ist aufgetreten.\n";
  exit;
}

$arr = pg_fetch_all($result);

print_r($arr);

?>


